import React from 'react';
import { View } from 'react-native';

const ProgressButtons = props => (
  <View style={{ flexDirection: 'row', marginTop: 50,width:'100%' }}>
    <View style={{position:'relative', width:'50%', bottom: 40}}>{props.renderPreviousButton()}</View>
    <View style={{position:'relative', width:'50%', bottom: 40}}>{props.renderNextButton()}</View>
  </View>
);

export default ProgressButtons;
